(836, 'Bridge Event: Helping Newcomers Succeed Through Effective Programs and Practices', '', '', '2016-03-08', '2016-03-08', '9:00 a.m.&ndash;3:30 p.m. CT', 'Houston', 'TX', 'Lone Star College University Park Conference Center', '', 'This free all-day event brings together experts to discuss issues of importance to those who work with newcomer students, newly arrived immigrant students who need to learn English.', '', '', '', 'http://relsouthwest.sedl.org/bridge_events/2016-03-08_newcomers/index.html', 'English Learners, ', '', 'rdrew', '2016-01-27', 'yes', 'yes', 'EL, NM-AG, TX, ', 'http://relsouthwest.sedl.org/bridge_events/2016-03-08_newcomers/index.html', 'no', '', ''),
(841, 'Carnegie Foundation Summit, Poster Session:&nbsp;Collaboration to Improve Rural Education,&nbsp;Oklahoma Rural Schools Research Alliance', '', '', '2016-03-22', '2016-03-24', '', 'San Francisco', 'CA', '', '', '<p class="p1"><span class="s1">This poster reviews how three alliance members use many of the Carnegie improvement principles to address educational challenges in rural Oklahoma. The overarching alliance theme is collaboration. The alliance structure, process, and membership provides a great example of how to leverage the team members&rsquo; expertise to address educational challenges.</span></p>\r\n<p class="p1"><span class="s1"><em>Haidee K. Williams,&nbsp;Research Alliance Liaison/Senior TA Consultant, American Institutes for Research<br > Sharon Dean, Associate Director, K20 Center for Educational and Community Renewal<br > Kathren Stehno, Senior Coordinator/Project Manager, Oklahoma Office of Educational Quality &amp; Accountability</em></span></p>', 'Haidee Williams', '', 'hkwilliams@air.org', 'http://www.carnegiefoundation.org/get-involved/events/summit-improvement-education/poster-session/', 'American Indian, Data Use, Rural, ', '', 'rdrew', '2016-02-19', 'yes', 'yes', 'OK, ', '', 'yes', 'March 22-24', ''),
(842, 'AERA Annual Meeting Poster Session:&nbsp;Connecting Research and Practice: Regional Educational Laboratory Partnerships to Improve Data Use in Education', '', '', '2016-04-12', '2016-04-12', '8:15&ndash;9:45 a.m.', 'Washington', 'DC', 'Convention Center, Level One, Room 101', '', 'In this structured poster session, the 10 Regional Educational Laboratories (RELs) will highlight how they help strengthen public education in the United States through research and technical assistance projects that focus on data systems, data use, and data analysis. REL researchers will present the challenges, opportunities, and lessons learned from their collaborations with school districts, state departments of education, and others as they provide support for a more evidence-reliant education system. <br ><br >REL Southwest will present the poster "Facilitating&nbsp;Professional Learning Communities That Support Struggling Readers Who Are English Learners in the Southwest."', 'Jackie Burniske', '', 'jburniske@air.org', 'http://tinyurl.com/z6mn9lz', 'English Learners, Hispanic, Research, ', '', 'jwackwit', '2016-03-11', 'yes', 'yes', 'National, EL, ', '', 'no', '', ''),
(843, 'Educator Effectiveness Research Alliance: Understanding Logic Models Workshop', '', '', '2016-05-16', '2016-05-16', '9 a.m.&ndash;4 p.m. CT', 'Austin', 'TX', 'American Institutes for Research, 4700 Mueller Blvd.', '', 'This workshop provided an overview of logic models, with a focus on their use as a planning, communication, and assessment tool with educator evaluation systems. In addition, facilitators led participants in developing logic models for their local educator evaluation systems. Participants then engaged in discussions about the short- and long-term goals of their evaluation systems and how best to use the logic models to support those goals.&nbsp;<br ><br >The workshop was designed for members of REL Southwest''s Educator Effectiveness Research Alliance and their colleagues. We encourage workplace teams to attend together.', 'Doug Fireside, Alliance Liaison', '', 'dfireside@air.org', '', 'Educator Effectiveness, ', '', 'jwackwit', '2016-04-07', 'yes', 'yes', 'EE, ', '', 'no', '', ''),
(847, 'Bridge Event: Culturally Responsive Resources for Native Students', '', '', '2016-05-11', '2016-05-11', '3:00&ndash;4:30 p.m. CT', 'Webinar', '', '', '', '<p>This free webinar explores high-quality online resources that support culturally responsive instruction for Native American students. Presenters from the Center on Standards &amp; Assessment Implementation and the National Indian Education Association describe the culturally responsive resources available in their online repositories.&nbsp;The archive for this event will be posted soon.</p>', 'Jackie Burniske, Dissemination Director, REL Southwest', '', 'jburniske@air.org', 'http://relsouthwest.sedl.org/bridge_events/2016-05-11_crrns/index.html', 'American Indian, ', '', 'jwackwit', '2016-04-18', 'yes', 'yes', 'AR, EE, EL, LA, NM-AG, NM-PK, OK, TX, ', 'http://relsouthwest.sedl.org/bridge_events/2016-05-11_crrns/index.html', 'no', '', ''),
(846, 'Early Warning Systems Technical Assistance Training', '', '', '2016-06-17', '2016-06-17', '9 a.m.&ndash;4 p.m. CT', 'Mission', 'TX', 'Sharyland Independent School District', '', '<p>This free all-day technical assistance training on early warning systems is designed for &nbsp;teams from participating school districts.</p>', 'Linda Cavazos, Alliance Liaison', '', 'lcavazos@air.org', '', 'English Learners, Hispanic, ', '', 'jwackwit', '2016-04-15', 'yes', 'yes', 'EL, ', '', 'no', '', ''),
