(855, 'Bridge Event:&nbsp;Research-Based Practices for Improving Academic Outcomes for English Learners', '', '', '2016-06-20', '2016-06-20', '1:00&ndash;1:45 p.m. CT', 'South Padre Island', 'TX', 'South Padre Island Convention Centre', '', '<p>Join us at the <strong>Effective Border Schools Conference</strong> for a free keynote presentation on research-based practices to support English learners at different proficiency levels. Diane August, Ph.D., a leading researcher in the field of second-language instruction, will describe and model effective ways to differentiate content-area instruction for English learners, with an emphasis on middle and high school students.</p>', 'Jackie Burniske', '', 'jburniske@air.org', 'http://relsouthwest.sedl.org/bridge_events/2016-06-20_elborderconf/index.html', 'English Learners, ', '', 'jwackwit', '2016-05-16', 'yes', 'yes', 'AR, EE, EL, LA, NM-AG, NM-PK, OK, TX, ', 'http://relsouthwest.sedl.org/bridge_events/2016-06-20_elborderconf/index.html', 'no', '', ''),
(856, 'REL Southwest Workshop: Research-Based Tools and Resources for Family and Community Engagement', '', 'National Indian Education Association Annual Convention', '2016-10-08', '2016-10-08', '9:00 &ndash; 10:15 a.m. PT', 'Reno', 'NV', 'Grand Sierra Resort & Casino, Carson 3', '', 'Join us at the NIEA Convention for a workshop on research-based practices to support parent and family engagement in rural and Native American communities. In addition, participants will explore ways to apply the information to improve their local family and community engagement practices, programs, and policies.<br ><br ><strong>Presenters:</strong> Erica Plut, Haidee Williams, and Jenna Zacamy, REL Southwest', 'Haidee Williams', '', 'hkwilliams@air.org', '', 'American Indian, ', '', 'jwackwit', '2016-06-02', 'yes', 'yes', 'NM-AG, OK, ', '', 'no', '', ''),
(857, 'REL Central and REL Southwest Workshop:&nbsp;Two Effective Models of Family and Community Engagement: Combining Research and Practice', '', 'National Indian Education Association Annual Convention', '2016-10-07', '2016-10-07', '8:00&ndash;9:15 a.m. PT', 'Reno', 'NV', 'Grand Sierra Resort & Casino, Carson 2', '', 'Join us at the NIEA Convention for a workshop on research-based&nbsp;practices for engaging Native American families and communities so students can thrive academically, culturally, socially, and emotionally. The session will begin with a review of the research and then explore how promising practices and principles are being implemented in the Native American Center at Fort Lewis College in Durango, Colorado, and through collaborative efforts of tribes from Oklahoma. REL Central and REL Southwest are co-presenting this workshop.<br ><br ><strong>Presenters:</strong> Linda Fredericks, REL Central; Haidee Williams, REL Southwest; Yvonne Bilinski, Native American Center; &amp; Norma Neely, American Indian Institute', 'Haidee Williams', '', 'hkwilliams@air.org', '', 'American Indian, ', '', 'jwackwit', '2016-06-02', 'yes', 'yes', 'NM-AG, OK, ', '', 'no', '', ''),
