(859, 'Bridge Event:&nbsp;Preschool to Kindergarten: Successfully Navigating the Transition', '', '', '2016-08-02', '2016-08-02', '2:30&ndash;4:00 p.m. CT', 'Webinar', '', '', '', 'Join REL Southwest for a free webinar on evidence-based practices to help children and their families navigate the transition to kindergarten. Presenters Margaret Caspe, Ph.D., senior research analyst at Harvard Family Research Project, and Jody Veit-Edrington, early childhood program coordinator for the North Little Rock School District, provide an overview of the current research on the transition to kindergarten as well as examples of promising practices districts are using to facilitate this transition.', 'Jackie Burniske', '', 'jburniske@air.org', 'http://relsouthwest.sedl.org/bridge_events/2016-08-02_kinder/index.html', 'Early Childhood/PreK, ', '', 'jwackwit', '2016-06-08', 'yes', 'yes', 'National, ', 'http://relsouthwest.sedl.org/bridge_events/2016-08-02_kinder/index.html', 'no', '', ''),
(861, 'Workshop: Using Data to Guide Instruction in a Culturally Responsive Environment', '', 'Louisiana Charter Schools Research Alliance', '2016-08-04', '2016-08-05', '', 'New Orleans', 'LA', 'Hynes Elementary School', '', 'This free workshop is designed for members of the&nbsp;Louisiana Charter Schools Research Alliance but is open to all Louisiana educators. Participants will discuss and better understand education data privacy laws, implications, and resources as well as the&nbsp;importance of data use to plan and implement culturally relevant instruction. Presenters and facilitators include Louisiana Department of Education (LDOE) staff, school leaders, data specialists, and teachers.', 'Darlene Brown, Alliance Liaison', '', 'dmbrown@air.org', '', '', '', 'jwackwit', '2016-07-19', 'yes', 'yes', 'LA, ', '', 'no', '', ''),
(862, 'Early Warning and Risk Prevention for English Learners', '', '', '2016-09-07', '2016-09-07', '2:30&ndash;4:00 p.m. CT', 'Webinar', '', '', '', 'This free webinar focuses on the use of early warning systems to identify and support English learners at risk of not meeting education milestones and academic goals. The presentation includes examples from districts with large populations of English learners.&nbsp;The event is open to the public.', 'Jackie Burniske', '', 'jburniske@air.org', 'http://relsouthwest.sedl.org/ews/2016-09-07-webinar/index.html', 'English Learners, Hispanic, ', '', 'jwackwit', '2016-08-05', 'yes', 'yes', 'EL, NM-AG, TX, ', 'http://relsouthwest.sedl.org/ews/2016-09-07-webinar/index.html', 'no', '', ''),
(863, 'Bridge Event: Unlocking Key Challenges Facing Arkansas'' Schools', '', '', '2016-09-14', '2016-09-14', '9:00 a.m.&ndash;3:00 p.m. CT', 'Little Rock', 'AR', 'Heifer International Educational Facilities', '', 'Join us for a free all-day conference on evidence-based practices related to high-quality prekindergarten programs, rural education, and instruction for diverse learners, with a focus on Arkansas. REL&nbsp;Southwest is cohosting this event with the Office for Education Policy at the University of Arkansas. This event is free and open to all.', 'Jackie Burniske', '', 'jburniske@air.org', 'http://relsouthwest.sedl.org/bridge_events/2016-09-14_aroep/index.html', 'Early Childhood/PreK, Rural, ', '', 'jwackwit', '2016-08-05', 'yes', 'yes', 'AR, ', 'http://relsouthwest.sedl.org/bridge_events/2016-09-14_aroep/index.html', 'no', '', ''),
