(1007, 'Bridge Event: Teacher Mentoring: Lessons Learned and Next Steps', '', '', '2017-08-30', '2017-08-30', '', 'Webinar', '', '', '', 'Join REL Southwest and the Center on Great Teachers and Leaders for a free webinar on implementing effective teacher mentoring programs, including lessons learned from research and the field.', 'Jackie Burniske', '', 'jburniske@air.org', 'http://relsouthwest.sedl.org/bridge_events/2017-08-30-mentoring/index.html', 'Educator Effectiveness, ', '', 'jwackwit', '2017-08-09', 'yes', 'yes', 'EE, ', 'http://relsouthwest.sedl.org/bridge_events/2017-08-30-mentoring/index.html', 'no', '', ''),
(1008, 'REL Southwest Session: Graduation Exam Performance and Course-Taking of English Learner High&nbsp;School Students in New Mexico', '', 'La&nbsp;Cosecha Dual Language Conference', '2017-11-01', '2017-11-04', '', 'Albuquerque', 'NM', 'Convention Center', '', 'Learn about a REL Southwest research study conducted in partnership with the New&nbsp;Mexico Achievement Gap Research Alliance. The study looked at the high school graduation exam performance of the 2011&ndash;2015 graduation cohorts, enrollment in algebra II and lab science for the 2014&ndash;2015 cohorts, and the relationship of exam performance and course enrollment with later graduation outcomes. Results for English learner students will be presented.', 'Jackie Burniske', '', 'jburniske@air.org', 'http://www.lacosechaconference.org', 'Achievement Gap, English Learners, Hispanic, ', '', 'jwackwit', '2017-08-17', 'yes', 'yes', 'NM-AG, ', '', 'no', '', ''),
(1009, 'Bridge Event:&nbsp;A Research Partnership to Support High-Quality Professional Learning', '', '', '2017-09-19', '2017-09-19', '2:30&ndash;4:00 p.m. CDT', 'Webinar', '', '', '', 'Join REL Southwest and the Oklahoma Rural Schools Research Alliance for a free webinar highlighting a new study on professional development practices in rural and nonrural schools in Oklahoma, including how the state is using the information to support high-quality professional learning.', 'Jackie Burniske', '', 'jburniske@air.org', 'http://relsouthwest.sedl.org/bridge_events/2017-09-19-okpd/', 'Educator Effectiveness, Rural, ', '', 'jwackwit', '2017-08-25', 'yes', 'yes', 'OK, ', 'http://relsouthwest.sedl.org/bridge_events/2017-09-19-okpd/', 'no', '', ''),
(1010, 'English Learners Research Alliance Quarterly Meeting&nbsp;&ndash;&nbsp;Central Texas', '', '', '2017-10-09', '2017-10-09', '9 a.m.&ndash;12 p.m. CT', 'Austin', 'TX', 'AIR Austin Office', '', '<p>Alliance members will convene to receive and discuss the results of the alliance''s studies and technical assistance projects, review other alliance work, and continue planning for sunsetting the alliance after the REL contract ends.</p>', 'Dalia Zabala, Alliance Liaison', '', 'dzabala@air.org', '', 'English Learners, ', '', 'jwackwit', '2017-09-06', 'yes', 'no', 'EL, ', '', 'no', '', ''),
(1011, 'English Learners Research Alliance Quarterly Meeting&nbsp;&ndash;&nbsp;South Texas', '', '', '2017-10-10', '2017-10-10', '9 a.m.&ndash;12 p.m. CT', 'Edinburg', 'TX', 'Region 1 Education Service Center', '', '<p>Alliance members will convene to receive and discuss the results of the alliance''s studies and technical assistance projects, review other alliance work, and continue planning for sunsetting the alliance after the REL contract ends.</p>', 'Dalia Zabala, Alliance Liaison', '', 'dzabala@air.org', '', 'English Learners, ', '', 'jwackwit', '2017-09-06', 'yes', 'no', 'EL, ', '', 'no', '', ''),
(1012, 'Arkansas Prekindergarten Research Alliance Quarterly Meeting', '', '', '2017-09-12', '2017-09-12', '8:30 a.m.&ndash;12:30 p.m.', 'Little Rock', 'AR', 'Holiday Inn Airport Conference Center', '', 'Alliance members will convene for their final face-to-face meeting. Agenda topics include a discussion of the results of the alliance''s projects and plans for sunsetting the alliance after the REL contract ends in November 2017.', 'Janice Keizer, Alliance Liaison', '', 'jkeizer@air.org', '', 'Early Childhood/PreK, ', '', 'jwackwit', '2017-09-06', 'yes', 'no', 'AR, ', '', 'no', '', ''),
(1013, 'New Mexico Achievement Gap Research Alliance Meeting', '', '', '2017-10-31', '2017-10-31', '1&ndash;5 p.m. MT', '', 'NM', '', '', '<p>Alliance members will convene to receive and discuss the results of alliance studies, review other alliance work, and continue planning for sunsetting the alliance after the REL contract ends.</p>', 'Carmen Martinez, Alliance Liaison', '', 'cmartinez@air.org', '', 'Achievement Gap, ', '', 'jwackwit', '2017-09-15', 'yes', 'no', 'NM-AG, ', '', 'no', '', ''),
(1014, 'New Mexico Achievement Gap Research Alliance Quarterly Meeting', '', '', '2017-09-28', '2017-09-28', '2&ndash;3 p.m. MT', 'Webinar', '', '', '', '<p>Alliance members will convene to receive the results of alliance studies and discuss&nbsp;how best to promote them, to review other alliance work, and to continue planning for sunsetting the alliance after the REL contract ends.</p>', 'Carmen Martinez, Alliance Liaison', '', 'cmartinez@air.org', '', 'Achievement Gap, ', '', 'jwackwit', '2017-09-15', 'yes', 'no', 'NM-AG, ', '', 'no', '', ''),
(1015, 'Bridge Event: Exploring Educator Movement Between Districts', '', 'REL Midwest with REL Southwest', '2017-10-04', '2017-10-04', '12:00&ndash;1:30 p.m. CT', 'Webinar', '', '', '', 'This webinar focuses on research addressing the teacher pipeline and the mobility of teachers between schools and districts. Presenters will discuss two published REL Midwest research studies on teacher mobility trends and strategies for estimating teacher supply and demand. Following each presentation, leaders from the Oklahoma State Department of Education and the Minnesota Department of Education will respond to the presentations and share state initiatives to meet teacher staffing needs. Presenters also will briefly highlight two upcoming REL Southwest studies related to teacher supply and demand.', 'Jackie Burniske', '', 'jburniske@air.org', 'https://ies.ed.gov/ncee/edlabs/regions/midwest/events/archived_events/2017/october_4.aspx', 'Educator Effectiveness, ', '', 'jwackwit', '2017-09-22', 'yes', 'yes', 'EE, OK, ', '', 'no', '', ''),
(1016, 'Bridge Event:&nbsp;Building a System of Great Schools: The What, So What, and Now What?', '', '', '2017-10-24', '2017-10-24', '12:00&ndash;1:30 p.m. EDT', 'Webinar', '', '', '', 'Do you know how many students in your district attend high-quality schools? And how can you systematically increase that number? Join us for a free webinar presenting actionable guidance on how to build a system of great schools through school portfolio management.', 'Jackie Burniske', '', 'jburniske@air.org', 'http://relsouthwest.sedl.org/bridge_events/2017-10-24-portfolio/index.html', 'Educator Effectiveness, ', '', 'jwackwit', '2017-10-11', 'yes', 'yes', 'EE, ', 'http://relsouthwest.sedl.org/bridge_events/2017-10-24-portfolio/index.html', 'no', '', ''),
(1017, 'Bridge Event: Closing the Achievement Gap: Research From New&nbsp;Mexico', '', '', '2017-11-08', '2017-11-08', '1:30&ndash;3:00 p.m. MDT /&nbsp;2:30&amp', 'Webinar', '', '', '', 'Join REL Southwest for a free webinar examining how New Mexico high school students&rsquo; advanced course completion and graduation rates changed after the state adopted more rigorous requirements.', 'Jackie Burniske', '', 'jburniske@air.org', 'http://relsouthwest.sedl.org/bridge_events/2017-11-08-nmag/index.html', 'Achievement Gap, ', '', 'jwackwit', '2017-10-25', 'yes', 'yes', 'NM-AG, ', 'http://relsouthwest.sedl.org/bridge_events/2017-11-08-nmag/index.html', 'no', '', '');
